let style
let editor
ace.config.set('basePath', 'https://cdn.jsdelivr.net/npm/ace-builds@1.23.1/')

const variables = {
    "text-colour": "white",
    "review-image-fallback-bg": "#ff3366",
    "star-colour": "#ff336699",
    "star-not-filled-colour": "#ff336633",
    "star-outline": "#ff3366",
    "review-item-background": "#322659"
}

/**
 * 
 * @param {string} text 
 */
const handleVariables = (text) => {
    text = text.replace(/\{\{(.*)\}\}/gm, (a,b,c) => {
        return `--${b}: ${variables[b]};`
    })
    return text
}

const updateStyle = () => {
    style.innerHTML = handleVariables(editor.getValue())
}

const generateButtons = () => {
    const container = document.getElementById("variables")

    const generateButton = ( key, value ) => {
        const b = document.createElement("button")
        b.classList = "button"
        b.style = `background-color: ${value}`
        b.type = "color"

        const text = document.createElement("span")
        text.classList = "button-text"
        text.innerText = key

        const inputElement = document.createElement("input")
        inputElement.classList = "colour-input"
        inputElement.type = "color"

        inputElement.addEventListener("change", (ev) => {
            const val = ev.target.value
            b.style = `background-color: ${val}`
            variables[key] = val
            updateStyle()
        })

        b.append(text, inputElement)

        return b
    }

    for(const [key, value] of Object.entries(variables)) {
        container.append(generateButton(key, value))
    }
    // variables
}

document.addEventListener("DOMContentLoaded", () => {
    document.getElementById("preview-content").innerHTML = load
    style = document.getElementById("preview-style")
    editor = ace.edit("editor");
    editor.setOptions({
        theme: "ace/theme/gruvbox",
        showGutter: false,
        showPrintMargin: false
    })

    editor.session.setMode("ace/mode/css")

    generateButtons()
    updateStyle()
})